import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";

import MainInput from "./MainInput";
import MessageWindow from "../../components/MessageWindow";
import WindowTopBar from "../../components/WindowTopBar";

class MainWindow extends Component {
  constructor(props) {
    super(props);
    this.state = {
      messages: []
    };
  }

  render() {
    return (
      <div className="main-window">
        <div className="main-window__top-bar">
          <WindowTopBar
            title={this.props.window.title}
            meta={this.props.window.meta}
          />
        </div>
        <div
          ref={element => (this.messageWindow = element)}
          className="main-window__message-window"
        >
          <MessageWindow messages={this.getOpenedMessage()} />
        </div>
        <MainInput
          wrappedComponentRef={mainInput => (this.mainInput = mainInput)}
          activeMessages={this.getOpenedMessage()}
        />
      </div>
    );
  }
}

export default withRouter(connect(state => ({}))(MainWindow));
