import React from "react";

import { useSelector } from "react-redux";

import ActiveChat from "../../components/ActiveChat";

const ActiveMessage = props => {
  const listReceiver = useSelector(state => state.activeMsg);

  // const receiver = Object.keys(listReceiver);

  return (
    <>
      {listReceiver.map((list, id) => (
        <div className={`contact`} onClick={props.onClick} id={id} key={id}>
          {list}
        </div>
      ))}
    </>
  );
};

export default ActiveMessage;

