import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";

import MainInput from "./MainInput";
import MessageWindow from "../../components/MessageWindow";
import WindowTopBar from "../../components/WindowTopBar";

import { sendMessage } from "../../redux/actions/MessageAction";

class MainWindow extends Component {
  state = {
    messages: "",
    sender: JSON.parse(localStorage.getItem("reduxState")).loginData.data.id
  };

  handleSubmit = e => {
    e.preventDefault();
    // const { messages } = this.state;
    console.log(e);

    if (e.target.value) {
      this.props.sendMessage(this.state);
    }
  };

  handleChange = e => {
    this.setState({ messages: e.target.value });
    console.log(this.state.messages);
  };

  handleKeyUp = e => {
    e.preventDefault();

    if (
      e.keyCode === 13 &&
      !e.ctrlKey &&
      this.handleSubmit &&
      this.state.messages.trim().length !== 0
    ) {
      this.handleSubmit(e);
      console.log(this.state);

      this.setState({ messages: "" });
    } else if (
      e.keyCode === 13 &&
      e.ctrlKey &&
      this.state.messages.trim().length !== 0
    ) {
      e.target.value = e.target.value + "\n";
      return true;
    }
  };

  render() {
    return (
      <div className="main-window">
        <div className="main-window__top-bar">
          <WindowTopBar />
        </div>
        <div className="main-window__message-window">
          <MessageWindow messages={this.props.messages} />
        </div>
        <MainInput
          onSubmit={this.handleSubmit}
          onChange={this.handleChange}
          onKeyUp={this.handleKeyUp}
          value={this.state.messages}
        />
      </div>
    );
  }
}

const mapStateToProps = state => ({
  messages: state.messages.messages
});

export default withRouter(
  connect(
    mapStateToProps,
    { sendMessage }
  )(MainWindow)
);
