import React from "react";

const MessageBox = props => {
  const myId = JSON.parse(localStorage.getItem("reduxState")).loginData.data.id;
  return (
    <div
      className={`message-box ${
        props.sender === myId ? "message-box--from-me" : ""
      } `}
      ref={el => {
        this.messagesEnd = el;
      }}
    >
      <div className="message-box__body">
        <div className="message-box__content">
          <div>
            {props.children}
            <br />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MessageBox;
