import React from "react";

import { useSelector } from "react-redux";

const ActiveMessage = props => {
  const listReceiver = useSelector(state => state.messages);

  const receiver = Object.keys(listReceiver);

  return (
    <>
      {receiver.map((list, id) => (
        <div onClick={props.onClick} id={id} key={id}>
          {list}
          <div className={`contact ${id ? "contact--active" : ""}`}>
            <img className="contact__image" scr={props.image} alt="" />
            <div className="contact__content">
              <div className="contact__title">{props.title}</div>
              <div className="contact__meta">{props.meta}</div>
            </div>
          </div>
        </div>
      ))}
    </>
  );
};

export default ActiveMessage;

