import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";

import InputWindow from "../../components/InputWindow";

import { sendMessage } from "../../redux/actions/MessageAction";

class MainInput extends Component {
  state = {
    messages: "",
    sender: JSON.parse(localStorage.getItem("reduxState")).loginData.data.id
  };

  scrollToBottom = () => {
    this.messagesEnd.scrollIntoView({ behavior: "smooth" });
  };

  componentDidMount() {
    this.scrollToBottom();
  }

  componentDidUpdate() {
    this.scrollToBottom();
  }

  handleSubmit = e => {
    e.preventDefault();
    // const { messages } = this.state;
    console.log(e);

    if (e.target.value) {
      this.props.sendMessage(this.state);
      this.scrollToBottom();
    }
  };

  handleChange = e => {
    this.setState({ messages: e.target.value });
    console.log(this.state.messages);
  };

  handleKeyUp = e => {
    e.preventDefault();

    if (
      e.keyCode === 13 &&
      !e.ctrlKey &&
      this.handleSubmit &&
      this.state.messages.trim().length !== 0
    ) {
      this.handleSubmit(e);
      console.log(this.state);

      this.setState({ messages: "" });
    } else if (
      e.keyCode === 13 &&
      e.ctrlKey &&
      this.state.messages.trim().length !== 0
    ) {
      e.target.value = e.target.value + "\n";
      return true;
    }
  };

  render() {
    return (
      <div className="main-window__input-window">
        <InputWindow
          placeholder="Say something..."
          onSubmit={this.handleSubmit}
          onChange={this.handleChange}
          onKeyUp={this.handleKeyUp}
          value={this.state.messages}
        />
      </div>
    );
  }
}

export default withRouter(
  connect(
    state => ({}),
    { sendMessage }
  )(MainInput)
);
