const initialState = {};

export default (state = initialState, action) => {
  switch (action.type) {
    case "INITIAL_CHAT":
      return {
        ...state,
        chat: action.payload
      };

    case "SEND_MESSAGE":
      if (state.messages.length === 0) {
        return {
          ...state,
          [action.name]: [
            {
              id: 1 + Math.random(),
              chat: action.payload,
              sender: action.sender
            }
          ]
        };
      } else {
        return {
          ...state,
          [action.name]: [
            ...state[action.name],
            {
              id: 1 + Math.random(),
              sender: action.sender,
              chat: action.payload
            }
          ]
        };
      }

    default:
      return state;
  }
};
