import Axios from "axios";

import { API } from "../../helpers/ApiHelper";

export const sendMessage = data => dispatch => {
  dispatch({
    type: "SEND_MESSAGE",
    receiver: "",
    payload: data.chat,
    sender: data.id_user_from,
    name: data.id_user_to
  });
  Axios.post(`${API}/messages`, data)
    .then(res => {
      dispatch({
        type: "SEND_MESSAGE",
        receiver: "",
        payload: data.chat,
        sender: data.id_user_from,
        name: data.id_user_to
      });
      console.log(res);
    })
    .catch(err => {
      console.log(err);
    });
};
