      //   payload: res.messages,
      //   sender: res.sender,
      //   name: res.name,
      // });
      console.log(res);
    })
    .catch(err => {
      // dispatch({
      //   type: "SEND_MESSAGE",
      //   payload: data.messages,
      //   sender: data.sender
      // });
      console.log(err);
    });
};

export const initialMsg = data => dispatch => {
  Axios.get(`${API}/chat/00${data.id}`)
    .then(res => {
      dispatch({ type: "INITIAL_CHAT", payload: res.data });
      console.log(res);
    })
    .catch(err => console.log(err));
};
