import React from "react";
import PropTypes from "prop-types";

import MessageBox from "../MessageBox";
import { useSelector } from "react-redux";
// import Badge from "../Badge";

const MessageWindow = props => {
  const listReceiver = useSelector(state => state.messages);

  const receiver = Object.keys(listReceiver);

  return (
    <div className="message-window">
      {listReceiver[receiver[props.idReceiver]].map((message, i) => (
        <MessageBox sender={message.sender} key={i}>
          {message.chat}
        </MessageBox>
      ))}
      {console.log("receiver :", listReceiver)}
    </div>
  );
};

MessageWindow.propTypes = {
  messages: PropTypes.array
};

export default MessageWindow;
