import React from 'react'
import PropTypes from 'prop-types'

import MessageBox from '../MessageBox';
import Badge from '../Badge'

const MessageWindow = (props) => (
    <div className="message-window">
        {
            props.messages && props.messages.map((message, index) => {
                const component = message.type && message.type === 'badge' ? <Badge key={`badge-${index}`} content={message.content} /> : <MessageBox 
                    key={`message-${message.messageType}${index}`} 
                    meta={message.meta} 
                    date={message.date} 
                    content={message.content} 
                    options={message.options} 
                    messageFromMyself={typeof message.messageFromMyself !== 'undefined' ? message.messageFromMyself : false} /> 
                return component
            })
        }
    </div>

);

MessageWindow.propTypes = {
    messages: PropTypes.array
}

export default MessageWindow;
