import React from "react";

import { useSelector } from "react-redux";

import ActiveChat from "../../components/ActiveChat"

const ActiveMessage = props => {
  const listReceiver = useSelector(state => state.messages);

  const receiver = Object.keys(listReceiver);

  return (
    <>
      {receiver.map((list, id) => (
        <ActiveChat onClick={props.onClick} id={id} key={id} title={list} />

      ))}
    </>
  );
};

export default ActiveMessage;

