import React from "react";

const MessageBox = props => {
  const myId = localStorage.getItem("reduxState")
    ? JSON.parse(localStorage.getItem("reduxState")).loginData.data.id
    : "";

  const myId2 = localStorage.getItem("reduxState")
    ? JSON.parse(localStorage.getItem("reduxState")).messages.data.map(
        dat => dat.id_user_from === myId
      )
    : "";

  // const Object1 = Object.keys(myId2) === "id_user_from";

  return (
    <div
      className={`message-box ${
        props.sender === myId || myId2 ? "message-box--from-me" : ""
      } `}
    >
      {console.log(
        "my id : ",
        myId,
        "my id 2 :",
        myId2,
        // "Object1 : ",
        // Object1,
        " props sender : ",
        props.sender
      )}
      <div className="message-box__body">
        <div className="message-box__content">
          <div>
            {props.children}
            <br />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MessageBox;
