import React from "react";

import { useSelector } from "react-redux";

const ActiveMessage = props => {
  const listReceiver = useSelector(state => state.messages);

  const receiver = Object.keys(listReceiver);

  return (
    <>
      {receiver.map((list, id) => (
        <div
          className={`contact ${props.active ? "contact--active" : ""}`}
          onClick={props.onClick}
        >
          <img className="contact__image" scr={props.image} alt="" />
          <div className="contact__content">
            {props.title && (
              <div className="contact__title">
                {" "}
                <div onClick={props.onClick} id={id} key={id}>
                  {list}
                </div>
              </div>
            )}
            {props.meta && <div className="contact__meta">{props.meta}</div>}
          </div>
          {props.date && <div className="contact__date">{props.date}</div>}
        </div>
      ))}
    </>
  );
};

export default ActiveMessage;

