import React from "react";
import { connect } from "react-redux";

class ActiveMessage extends React.Component {
  state = {};
  render() {
    return (
      <div>
        {this.props.ActiveMessage.map(chats => (
          <div>{chats}</div>
        ))}
      </div>
    );
  }
}

export default connect(state => ({ ActiveMessage: state.messages }))(
  ActiveMessage
);
