import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";

import MainInput from "./MainInput";
import MessageWindow from "../../components/MessageWindow";
import WindowTopBar from "../../components/WindowTopBar";

import { sendMessage } from "../../redux/actions/MessageAction";

class MainWindow extends Component {
  constructor(props) {
    super(props);
    this.state = {
      messages: []
    };
  }

  UNSAFE_componentDidUpdate = () => {
    this.scrollToLastMessage();
  };
  scrollToLastMessage = () => {
    this.messageWindow.scrollTop = this.messageWindow.scrollHeight;
  };
  render() {
    return (
      <div className="main-window">
        <div className="main-window__top-bar">
          <WindowTopBar />
        </div>
        <div className="main-window__message-window">
          <MessageWindow messages={this.props.messages} />
        </div>
        <MainInput />
      </div>
    );
  }
}

const mapStateToProps = state => ({
  messages: state.messages.messages
});

export default withRouter(
  connect(
    mapStateToProps,
    { sendMessage }
  )(MainWindow)
);
