const initialState = {
  activeChats: {}
};

export default (state = initialState, action) => {
  switch (action.type) {
    case "INITIAL_CHAT":
      return {
        ...state,
        activeChats: { ...state.activeChats, data: action.payload }
      };

    case "CREATE_NEW_CHAT":
      return {
        ...state,
        activeChats: {
          ...state.activeChats,
          [action.payload.username]: [action.payload]
        }
      };

    case "SEND_MESSAGE":
      if (state.activeChats.length === 0) {
        return {
          ...state,
          activeChats: [
            ...state.activeChats,
            {
              [action.name]: [
                {
                  id: 1 + Math.random(),
                  chat: action.payload,
                  sender: action.sender
                }
              ]
            }
          ]
        };
      } else {
        return {
          ...state,
          [action.name]: [
            ...state[action.name],
            {
              id: 1 + Math.random(),
              sender: action.sender,
              chat: action.payload
            }
          ]
        };
      }

    default:
      return state;
  }
};
