import React from "react";
import PropTypes from "prop-types";

import Contact from "../Contact";

import Axios from "axios";

const ContactWindow = props => {
  const [dataSearch, toSearch] = React.useState("");

  const handleChange = e => {
    toSearch(e.target.value);
  };

  const handleSubmit = () => {
    Axios.post("http://rocky-sierra-75836.herokuapp.com/api/search/user", {
      username: dataSearch
    })
      .then(res => console.log(res))
      .catch(err => console.log(err));
    console.log(dataSearch);
  };

  return (
    <>
      <div
        className="input-group"
        onSubmit={handleSubmit}
        onKeyUp={handleSubmit}
      >
        <input
          className="input-text"
          type="text"
          placeholder="Search contact... "
          onChange={handleChange}
        />
      </div>
      <div className="contact-window">
        {props.contacts &&
          props.contacts.map((contact, index) => (
            <Contact
              key={`contact-${contact.id}`}
              onClick={() =>
                props.onContactClick && props.onContactClick(contact.id)
              }
              image={contact.image}
              title={contact.title}
              meta={contact.meta}
              date={contact.date}
              active={props.activeContactId === contact.id ? true : false}
            />
          ))}
      </div>
    </>
  );
};

ContactWindow.propTypes = {
  contacts: PropTypes.array,
  onContactClick: PropTypes.func,
  activeContactId: PropTypes.oneOfType([PropTypes.string, PropTypes.number])
};

export default ContactWindow;
