const initialState = {
  messageSatu: [
    { id: 1, chat: "chat 1" },
    { id: 2, chat: "chat 2" },
    { id: 3, chat: "" }
  ],
  messageDua: [
    { id: 1, chat: "chat 1" },
    { id: 2, chat: "chat 2" },
    { id: 3, chat: "" }
  ],
  abc101: {
    title: "Vasel",
    meta: "Apa Kabar ",
    date: "Sep 20 2019",
    image: "./public/images/cat8.jpg",
    messages: [
      {
        type: "badge",
        content: "Sep 15 1019, 08:32"
      },
      {
        type: "badge",
        content: "you joined the conversation"
      },
      {
        type: "message",
        content: "Assalamualaikum",
        meta: "vasel",
        date: "Sep 20 2019, 11:21"
      },
      {
        type: "message",
        content: "Apa Kabar",
        meta: "vasel",
        date: "Sep 20 2019, 11:21"
      }
    ]
  }
};

export default (state = initialState, action) => {
  switch (action.type) {
    case "SEND_MESSAGE":
      return {
        ...state,
        message: [
          ...state.message,
          {
            ...state.messages,
            id: 1 + Math.random(),
            chat: action.payload
          }
        ]
      };

    default:
      return state;
  }
};
