import React from "react";

import { useSelector } from "react-redux";

import ActiveChat from "../../components/ActiveChat";

import Contact from "../Contact";

const ActiveMessage = props => {
  const listReceiver = useSelector(state => state.messages);

  // const receiver = Object.keys(listReceiver);

  return (
    <>
      <div
        className="input-group"
        // onSubmit={handleSubmit}
        // onKeyUp={handleSubmit}
      >
        <input
          className="input-text"
          type="text"
          placeholder="Search contact... "
          // onChange={handleChange}
        />
      </div>
      <div className="contact-window">
        {console.log("list receiver:", listReceiver)}
        {listReceiver
          ? listReceiver.search.map((contact, index) => (
              <>
                <Contact
                  key={`contact-${contact.id}`}
                  onClick={() => {
                    // dispatch(createNewChat(listReceiver.search[index]));
                    // props.history.push("/");
                  }}
                  image={contact.photo}
                  title={contact.username}
                  meta={contact.phone}
                  date={contact.updated_at}
                  active={props.activeContactId === contact.id ? true : false}
                />
                {/* {console.log("contact", resSearch.search[index])} */}
              </>
            ))
          : ""}
      </div>
    </>
  );
};

export default ActiveMessage;

