import Axios from "axios";

import { API } from "../../helpers/ApiHelper";

export const sendMessage = data => dispatch => {
  const disData = {
    chat: data.messages,
    id_user_from: data.sender,
    id_user_to: 1
  };
  dispatch({
    type: "SEND_MESSAGE",
    receiver: "",
    payload: data.messages,
    sender: data.sender,
    name: data.name
  });
  Axios.post(`${API}/messages`, disData)
    .then(res => {
      const token = res.data.access_token;
      localStorage.setItem("token", token);
      // dispatch({
      //   type: "SEND_MESSAGE",
      //   receiver: "",
      //   payload: res.messages,
      //   sender: res.sender,
      //   name: res.name,
      // });
      console.log(res);
    })
    .catch(err => {
      dispatch({
        type: "SEND_MESSAGE",
        payload: data.messages,
        sender: data.sender
      });
      // console.log(err);
    });
};
