import React from "react";
import { connect } from "react-redux";

import { useSelector, useDispatch } from "react-redux";
import activeMsgReducer from "../../redux/reducers/activeMsgReducer";
import { changeActive } from "../../redux/actions/activeMsgAction";

const ActiveMessage = () => {
  const listReceiver = useSelector(state => state);
  const dispatch = useDispatch();

  const receiver = Object.keys(listReceiver);

  const [activeReceiver, changeReceiver] = React.useState(receiver[0]);

  return (
    <>
      {receiver.map(list => (
        <div
          onClick={e => {
            dispatch(changeActive(e.target.value));
            changeReceiver(e.target.innerText);
            console.log(activeReceiver);
          }}
        >
          {list}
        </div>
      ))}
    </>
  );
};

export default ActiveMessage;

