import React from "react";

import { useSelector } from "react-redux";

const ActiveMessage = props => {
  const listReceiver = useSelector(state => state);

  const receiver = Object.keys(listReceiver);

  return (
    <>
      {receiver.map(list => (
        <div onClick={props.onClick}>{list}</div>
      ))}
    </>
  );
};

export default ActiveMessage;

