import React from "react";

const MessageBox = props => {
  const myId = localStorage.getItem("reduxState")
    ? JSON.parse(localStorage.getItem("reduxState")).loginData.data.id
    : "";

  const myId2 = JSON.parse(
    localStorage.getItem("reduxState")
  ).messages.data.filter(dat => dat.id_user_from === myId);

  return (
    <div
      className={`message-box ${
        props.sender === myId || (myId && myId2.id_user_from)
          ? "message-box--from-me"
          : ""
      } `}
    >
      {console.log(
        "my id : ",
        myId,
        "my id 2 :",
        myId2,
        " props sender : ",
        props.sender
      )}
      <div className="message-box__body">
        <div className="message-box__content">
          <div>
            {props.children}
            <br />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MessageBox;
